import { createTheme } from '@mui/material/styles';

// Warm yellow palette
const theme = createTheme({
  palette: {
    primary: {
      main: '#FFD54F', // warm yellow
      contrastText: '#333',
    },
    secondary: {
      main: '#FFA000', // deeper amber
      contrastText: '#fff',
    },
    background: {
      default: '#FFFDE7', // very light yellow
      paper: '#FFF9C4', // pale yellow
    },
    text: {
      primary: '#333',
      secondary: '#795548', // warm brown for accents
    },
  },
  shape: {
    borderRadius: 12,
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 16,
        },
      },
    },
  },
});

export default theme;
