import React, { useState, useEffect } from 'react';
import { Container, Typography, Box, Button, Snackbar, Alert, ThemeProvider, CssBaseline, Tabs, Tab } from '@mui/material';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import AuthHeader from './components/AuthHeader';
import { TaskWithSubTasks, SubTask, PromptSelection } from './services/geminiService';
import { fetchUserTasks, upsertUserTasks } from './services/supabaseTasksService';
import theme, { themes } from './theme';
import { createTheme } from '@mui/material/styles';
import Popover from '@mui/material/Popover';
import IconButton from '@mui/material/IconButton';

// App version: increment by 0.01 for each change
const APP_VERSION = '1.01'; // Updated for My Rewards tab + versioning

export interface Task { // Base Task interface (for potential future use without subtasks)
  id: string;
  title: string;
  completed: boolean;
}

type ThemeKey = 'sunbeam' | 'fresh' | 'night';
const themeKeys: ThemeKey[] = ['sunbeam', 'fresh', 'night'];

import { deleteUserTasks, deleteUserTasksById } from './services/supabaseTasksService';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<TaskWithSubTasks[]>([]);
  const [promptSelection, setPromptSelection] = useState<PromptSelection>('A');
  const [user, setUser] = useState<any>(null);
  const [mergeNotice, setMergeNotice] = useState(false);
  const [tab, setTab] = useState(0); // 0: My Tasks, 1: My Accomplishments
  const [deleteNotice, setDeleteNotice] = useState(false);

  // Theme switcher state
  // Persist theme choice
  const [themeKey, setThemeKey] = useState<ThemeKey>(() => {
    const stored = localStorage.getItem('themeKey');
    if (stored && themeKeys.includes(stored as ThemeKey)) return stored as ThemeKey;
    return 'sunbeam';
  });
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const currentTheme = themes[themeKey].theme;

  // Save theme to localStorage on change
  useEffect(() => {
    localStorage.setItem('themeKey', themeKey);
  }, [themeKey]);

  const handleTabChange = (_event: React.SyntheticEvent, newValue: number) => {
    setTab(newValue);
  };

  const incompleteTasks = tasks.filter(t => !t.completed);
  const completedTasks = tasks.filter(t => t.completed);

  // Load tasks from localStorage on initial mount (if not logged in)
  useEffect(() => {
    if (!user) {
      try {
        const storedTasks = localStorage.getItem('tasks');
        if (storedTasks) {
          const parsedTasks: TaskWithSubTasks[] = JSON.parse(storedTasks);
          // Convert dueDate strings back to Date objects
          const tasksWithDates = parsedTasks.map(task => ({
            ...task,
            dueDate: task.dueDate ? new Date(task.dueDate) : null
          }));
          setTasks(tasksWithDates);
          console.log('Tasks loaded from localStorage');
        }
      } catch (error) {
        console.error('Failed to load tasks from localStorage:', error);
      }
    }
  }, [user]); // Only run when user changes

  // Handler to delete a task
  const handleDeleteTask = async (taskId: string) => {
    try {
      setTasks(prev => prev.filter(task => task.id !== taskId));
      if (user && user.id) {
        // Delete from Supabase
        // (Assumes deleteUserTasks can be adapted to delete a single task by id)
        await deleteUserTasksById(user.id, taskId);
      } else {
        // Update localStorage
        const stored = localStorage.getItem('tasks');
        if (stored) {
          const tasksArr = JSON.parse(stored).filter((task: any) => task.id !== taskId);
          localStorage.setItem('tasks', JSON.stringify(tasksArr));
        }
      }
      setDeleteNotice(true);
    } catch (err) {
      console.error('Failed to delete task:', err);
    }
  };

  // Save tasks to localStorage only if not logged in
  useEffect(() => {
    if (!user) {
      try {
        // Convert Date objects to ISO strings before saving
        const tasksToStore = tasks.map(task => ({
          ...task,
          dueDate: task.dueDate ? task.dueDate.toISOString() : null
        }));
        localStorage.setItem('tasks', JSON.stringify(tasksToStore));
      } catch (error) {
        console.error('Failed to save tasks to localStorage:', error);
      }
    }
  }, [tasks, user]);

  // On login: sync/merge local tasks to Supabase, then clear local
  useEffect(() => {
    const doSync = async () => {
      if (user && user.id) {
        // 1. Fetch cloud tasks
        let cloudTasks: TaskWithSubTasks[] = [];
        try {
          cloudTasks = await fetchUserTasks(user.id);
        } catch (err) {
          console.error('Failed to fetch cloud tasks:', err);
        }
        // 2. Load local tasks
        let localTasks: TaskWithSubTasks[] = [];
        try {
          const stored = localStorage.getItem('tasks');
          if (stored) {
            localTasks = JSON.parse(stored).map((task: any) => ({
              ...task,
              dueDate: task.dueDate ? new Date(task.dueDate) : null
            }));
          }
        } catch {}
        // 3. Merge: find local tasks not in cloud by ID
        const cloudIds = new Set(cloudTasks.map(t => t.id));
        const newLocalTasks = localTasks.filter(t => !cloudIds.has(t.id));
        let mergedTasks = [...cloudTasks, ...newLocalTasks];
        if (newLocalTasks.length > 0) {
          // 4. Upload merged tasks to Supabase
          try {
            await upsertUserTasks(user.id, mergedTasks);
            setMergeNotice(true);
          } catch (err) {
            console.error('Failed to upload merged tasks:', err);
          }
        }
        // 5. Clear local storage and use cloud
        localStorage.removeItem('tasks');
        setTasks(mergedTasks);
      }
    };
    doSync();
    // eslint-disable-next-line
  }, [user]);

  // Updated to accept task data object
  const addTask = async (taskData: Omit<TaskWithSubTasks, 'id' | 'completed'>) => {
    // Do not generate a fake id for Supabase
    const newTaskForSupabase = {
      ...taskData,
      completed: false,
      subTasks: taskData.subTasks.map(st => ({...st, completed: st.completed ?? false }))
    };
    // Locally, temporarily assign a random id for optimistic UI (will be replaced on sync)
    const tempId = Date.now().toString() + Math.random().toString(36).substring(2, 9);
    const newTaskForLocal: TaskWithSubTasks = {
      ...newTaskForSupabase,
      id: tempId,
    };
    const updatedTasks = [...tasks, newTaskForLocal];
    setTasks(updatedTasks);
    if (user && user.id) {
      try {
        // upsertUserTasks should not send the temp id, so filter it out before sending
        const tasksForSupabase = updatedTasks.map(({id, ...rest}) => rest);
        await upsertUserTasks(user.id, tasksForSupabase);
      } catch (err) {
        console.error('Failed to sync new task to Supabase:', err);
      }
    }
  };

  // Handler to toggle main task and its subtasks
  const handleToggleTask = async (taskId: string) => {
    const updated = tasks.map(task => {
      if (task.id === taskId) {
        const newCompletedStatus = !task.completed;
        return {
          ...task,
          completed: newCompletedStatus,
          subTasks: task.subTasks.map(sub => ({ ...sub, completed: newCompletedStatus }))
        };
      }
      return task;
    });
    setTasks(updated);
    if (user && user.id) {
      try {
        await upsertUserTasks(user.id, updated);
      } catch (err) {
        console.error('Failed to sync toggle to Supabase:', err);
      }
    }
  };

  // Handler to toggle a single subtask and update parent task if needed
  const handleToggleSubTask = async (taskId: string, subTaskId: string | number) => {
    const updated = tasks.map(task => {
      if (task.id === taskId) {
        let allSubTasksCompleted = true;
        const updatedSubTasks = task.subTasks.map(sub => {
          if (sub.id === subTaskId) {
            const newSubCompletedStatus = !sub.completed;
            if (!newSubCompletedStatus) allSubTasksCompleted = false;
            return { ...sub, completed: newSubCompletedStatus };
          } else {
            if (!sub.completed) allSubTasksCompleted = false;
          }
          return sub;
        });
        return { ...task, subTasks: updatedSubTasks, completed: allSubTasksCompleted };
      }
      return task;
    });
    setTasks(updated);
    if (user && user.id) {
      try {
        await upsertUserTasks(user.id, updated);
      } catch (err) {
        console.error('Failed to sync subtask toggle to Supabase:', err);
      }
    }
  };

  // Function to toggle prompt
  const togglePrompt = () => {
    setPromptSelection(prev => (prev === 'A' ? 'B' : 'A'));
  };

  // Theme selector handlers
  // Theme selector handlers
  const handleThemeClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleThemeClose = () => setAnchorEl(null);
  const handleThemeSelect = (key: ThemeKey) => {
    setThemeKey(key);
    setAnchorEl(null);
  };


  return (
    <ThemeProvider theme={currentTheme}>
      <CssBaseline />
      {/* Restore AuthHeader for Google login and user state sync */}
      <AuthHeader onAuthChange={setUser} />
      <Container maxWidth="md" sx={{ position: 'relative' }}>
        {/* Version number in top right */}
        <Box sx={{ position: 'absolute', top: 16, right: 24, zIndex: 10, display: 'flex', alignItems: 'center', gap: 2 }}>
          {/* Theme Switcher */}
          <IconButton
            size="small"
            onClick={handleThemeClick}
            sx={{
              width: 28, height: 28, p: 0.5,
              border: '2px solid #eee',
              background: currentTheme.palette.primary.main,
              boxShadow: 2,
              mr: 1,
              '&:hover': { boxShadow: 4, background: currentTheme.palette.primary.dark || currentTheme.palette.primary.main }
            }}
            aria-label="Change theme"
          />
          <Popover
            open={Boolean(anchorEl)}
            anchorEl={anchorEl}
            onClose={handleThemeClose}
            anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
            transformOrigin={{ vertical: 'top', horizontal: 'left' }}
            PaperProps={{ sx: { p: 2, display: 'flex', gap: 2, boxShadow: 4 } }}
          >
            {themeKeys.map((key) => (
              <Box key={key} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mx: 1 }}>
                <IconButton
                  size="small"
                  sx={{
                    width: 32, height: 32, p: 0.5,
                    border: themeKey === key ? '2.5px solid #333' : '2px solid #eee',
                    background: themes[key].theme.palette.primary.main,
                    boxShadow: themeKey === key ? 4 : 1,
                    mb: 0.5,
                    transition: 'border 0.2s, box-shadow 0.2s',
                    '&:hover': { boxShadow: 6, background: themes[key].theme.palette.primary.dark || themes[key].theme.palette.primary.main }
                  }}
                  aria-label={`Switch to ${themes[key].name} theme`}
                  onClick={() => handleThemeSelect(key)}
                />
                <Typography
                  variant="caption"
                  sx={{ color: themeKey === key ? '#333' : '#888', fontWeight: themeKey === key ? 700 : 400, fontSize: 12 }}
                  aria-label={themes[key].name}
                >
                  {themes[key].name}
                </Typography>
              </Box>
            ))}
          </Popover>
          {/* Medal Icon and My Rewards */}
          <span style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
            <svg xmlns="http://www.w3.org/2000/svg" height="20" viewBox="0 0 24 24" width="20" fill="#FFA000" style={{ marginRight: 4 }}><path d="M0 0h24v24H0z" fill="none"/><path d="M12 17.27L18.18 21l-1.64-7.03L22 9.24l-7.19-.61L12 2 9.19 8.63 2 9.24l5.46 4.73L5.82 21z"/></svg>
            <Typography variant="caption" color="text.secondary" sx={{ fontWeight: 600 }}>My Rewards</Typography>
          </span>
          <Typography variant="caption" color="text.secondary" sx={{ ml: 2 }}>
            v{APP_VERSION}
          </Typography>
        </Box>
        <Box sx={{ my: 4 }}>
          {/* Prompt Toggle Button */}
          <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
            <Button variant="outlined" onClick={togglePrompt}>
              Using Prompt: {promptSelection} (Click to Switch)
            </Button>
          </Box>

          <AddTaskForm onAddTask={addTask} currentPrompt={promptSelection} user={user} />
          <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
            <Tabs value={tab} onChange={handleTabChange} variant="fullWidth">
              <Tab label="To Do" />
              <Tab label="My Accomplishments" />
            </Tabs>
          </Box>
          {tab === 0 && (
            // To Do: show only incomplete tasks
            <TaskList tasks={incompleteTasks} onToggleTask={handleToggleTask} onToggleSubTask={handleToggleSubTask} onDeleteTask={handleDeleteTask} user={user} />
          )}
          {tab === 1 && (
            // My Accomplishments: show only completed tasks
            <TaskList tasks={completedTasks} onToggleTask={handleToggleTask} onToggleSubTask={handleToggleSubTask} onDeleteTask={handleDeleteTask} user={user} />
          )}
        </Box>
      </Container>
      <Snackbar open={mergeNotice} autoHideDuration={4000} onClose={() => setMergeNotice(false)} anchorOrigin={{vertical:'top',horizontal:'center'}}>
        <Alert onClose={() => setMergeNotice(false)} severity="success" sx={{ width: '100%' }}>
          Local tasks have been uploaded and merged with your account!
        </Alert>
      </Snackbar>
      <Snackbar open={deleteNotice} autoHideDuration={3000} onClose={() => setDeleteNotice(false)} anchorOrigin={{vertical:'bottom',horizontal:'center'}}>
        <Alert onClose={() => setDeleteNotice(false)} severity="info" sx={{ width: '100%' }}>
          Task deleted!
        </Alert>
      </Snackbar>
    </ThemeProvider>
  );
};

export default App;
