import React, { useState } from 'react';
import { Container, Typography, Box } from '@mui/material';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import { TaskWithSubTasks, SubTask } from './services/geminiService';

export interface Task { // Base Task interface (for potential future use without subtasks)
  id: string;
  title: string;
  completed: boolean;
}

const App: React.FC = () => {
  const [tasks, setTasks] = useState<TaskWithSubTasks[]>([]);

  const addTask = (title: string, subTasks: SubTask[]) => {
    const newTask: TaskWithSubTasks = {
      id: Date.now().toString(),
      title,
      completed: false,
      subTasks
    };
    setTasks([...tasks, newTask]);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Task App
        </Typography>
        <AddTaskForm onAddTask={addTask} />
        <TaskList tasks={tasks} />
      </Box>
    </Container>
  );
};

export default App;
