import React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemIcon from '@mui/material/ListItemIcon';
import Checkbox from '@mui/material/Checkbox';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import Typography from '@mui/material/Typography';

// Define the structure of a subtask
interface Subtask {
  id: string | number;
  title: string;
  dependsOn: (string | number)[];
  timeEstimate?: string;
  completed: boolean;
}

// Define the props for the SubtaskList component
interface SubtaskListProps {
  subtasks: Subtask[];
  onToggleSubTask: (subTaskId: string | number) => void;
}

const SubtaskList: React.FC<SubtaskListProps> = ({ subtasks, onToggleSubTask }) => {
  if (!subtasks || subtasks.length === 0) {
    return <Typography variant="body2" color="text.secondary" sx={{ pl: 4, mt: 1 }}>No subtasks generated.</Typography>;
  }

  // Helper to find a subtask title by ID for dependency chips
  const getSubtaskTitleById = (id: string | number): string => {
    const dependentTask = subtasks.find(task => task.id === id);
    return dependentTask ? (dependentTask.title.substring(0, 15) + (dependentTask.title.length > 15 ? '...' : '')) : `ID: ${id}`;
  };

  return (
    <List dense sx={{ width: '100%', bgcolor: 'background.paper', mt: 1, pl: 4 }}>
      {subtasks.map((subtask) => (
        <ListItem
          key={subtask.id}
          disableGutters
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
            mb: 1,
            py: 0.5,
            opacity: subtask.completed ? 0.6 : 1,
          }}
        >
          <Stack direction="row" alignItems="center" sx={{ width: '100%' }}>
            <ListItemIcon sx={{ minWidth: 'auto', mr: 1 }}>
              <Checkbox
                checked={subtask.completed}
                onChange={() => onToggleSubTask(subtask.id)}
                size="small"
              />
            </ListItemIcon>
            <ListItemText
              primary={subtask.title}
              primaryTypographyProps={{
                sx: { textDecoration: subtask.completed ? 'line-through' : 'none' }
              }}
              secondary={subtask.timeEstimate ? `Est: ${subtask.timeEstimate}` : 'No estimate'}
              secondaryTypographyProps={{ variant: 'caption' }}
              sx={{ mb: subtask.dependsOn.length > 0 ? 0.5 : 0 }}
            />
          </Stack>

          {subtask.dependsOn.length > 0 && (
            <Stack direction="row" spacing={0.5} sx={{ pl: 5, pt: 0.5 }}>
              <Typography variant="caption" color="text.secondary" sx={{ mr: 0.5 }}>
                Depends on:
              </Typography>
              {subtask.dependsOn.map((depId) => (
                <Chip
                  key={`${subtask.id}-dep-${depId}`}
                  label={getSubtaskTitleById(depId)}
                  size="small"
                  variant="outlined"
                  title={`Depends on: ${getSubtaskTitleById(depId)} (ID: ${depId})`}
                />
              ))}
            </Stack>
          )}
        </ListItem>
      ))}
    </List>
  );
};

export default SubtaskList;
