import React from 'react';
import { List, ListItem, ListItemText, ListItemIcon, Checkbox, Paper, Typography, Box } from '@mui/material';
import { TaskWithSubTasks, SubTask } from '../services/geminiService';

interface TaskListProps {
  tasks: TaskWithSubTasks[];
  onToggleTask: (taskId: string) => void;
  onToggleSubTask: (taskId: string, subTaskId: string) => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, onToggleTask, onToggleSubTask }) => {
  return (
    <Paper elevation={2} sx={{ mt: 3, p: 2 }}>
      {tasks.length === 0 ? (
        <ListItem>
          <ListItemText primary="No tasks yet. Add one above!" />
        </ListItem>
      ) : (
        <List>
          {tasks.map((task) => (
            <Paper key={task.id} elevation={2} sx={{ mb: 2, p: 2, opacity: task.completed ? 0.6 : 1 }}>
              <ListItem>
                <ListItemIcon>
                  <Checkbox 
                    checked={task.completed} 
                    onChange={() => onToggleTask(task.id)} 
                  />
                </ListItemIcon>
                <ListItemText 
                  primary={task.title} 
                  primaryTypographyProps={{
                    variant: 'h6', 
                    sx: { textDecoration: task.completed ? 'line-through' : 'none' } 
                  }}
                  secondary={task.totalEstimatedTime || ''} 
                  secondaryTypographyProps={{ variant: 'caption' }}
                />
              </ListItem>
              {task.subTasks && task.subTasks.length > 0 && (
                <List sx={{ pl: 4 }}>
                  {task.subTasks.map((subTask: SubTask) => (
                    <ListItem 
                      key={subTask.id} 
                      sx={{ 
                        py: 0.5, 
                        opacity: subTask.completed ? 0.6 : 1 
                      }}
                      disablePadding
                    >
                      <ListItemIcon sx={{ minWidth: 'auto', mr: 1 }}>
                        <Checkbox 
                          checked={subTask.completed} 
                          onChange={() => onToggleSubTask(task.id, subTask.id)} 
                          size="small"
                        />
                      </ListItemIcon>
                      <ListItemText 
                        primary={subTask.title} 
                        primaryTypographyProps={{ 
                          sx: { textDecoration: subTask.completed ? 'line-through' : 'none' } 
                        }}
                        secondary={`${subTask.estimatedTime || ''}`}
                        secondaryTypographyProps={{ variant: 'caption' }}
                      />
                    </ListItem>
                  ))}
                </List>
              )}
            </Paper>
          ))}
        </List>
      )}
    </Paper>
  );
};

export default TaskList;
