import { GoogleGenerativeAI, HarmCategory, HarmBlockThreshold } from "@google/generative-ai";

const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

if (!GEMINI_API_KEY) {
  throw new Error("Gemini API key (REACT_APP_GEMINI_API_KEY) is not defined in the environment variables.");
}

export const geminiService = new GoogleGenerativeAI(GEMINI_API_KEY);

export interface SubTask {
  id: string;
  title: string;
  completed: boolean;
  estimatedTime?: string; // Optional estimated time
}

export interface Task {
  id: string;
  title: string;
  completed: boolean;
}

export interface TaskWithSubTasks extends Task {
  subTasks: SubTask[];
}

export async function breakDownTask(taskTitle: string): Promise<SubTask[]> {
  try {
    const genAI = geminiService; // Use the initialized service
    const model = genAI.getGenerativeModel({
      model: "gemini-1.5-flash", // Changed model name
    });

    const prompt = `
    Break down the following task into 3-5 actionable sub-tasks that are specific, measurable, achievable, relevant, and time-bound (SMART).
    For each sub-task, provide a rough estimate of how long it would take to complete in minutes.

    Task: ${taskTitle}

    Format your response as JSON with this structure:
    [
      {
        "title": "Sub-task description",
        "estimatedTime": "X minutes"
      }
    ]
    `;

    const result = await model.generateContent(prompt);
    const response = await result.response;
    const text = await response.text();

    try {
      const parsed = JSON.parse(text);
      return parsed.map((subtask: any, index: number) => ({
        id: `${Date.now()}-${index}`,
        title: subtask.title,
        estimatedTime: subtask.estimatedTime,
        completed: false
      }));
    } catch (error) {
      console.error('Failed to parse Gemini response:', error);
      throw new Error('Failed to parse task breakdown response');
    }
  } catch (error) {
    console.error('Error breaking down task:', error);
    throw error;
  }
};
